import React, { Component } from "react";
import styled from 'styled-components';
import {Link} from 'react-router-dom'
import './UpcomingEventsComponent.css'
import JxAsia2020Img from "../../../../images/JxAsia2020/JunctionX_2020_intro.jpg"


// INFO COMPONENT - comprises of a large top element and a smaller bottom element
const ImageBox = styled.div`
  width: 800px;
  height: 200px;
`


const TopBigLetter = styled.div`
  font-size: 100px;
  fontwight: 900;
  text-align: center;
  line-height: 100px;
`

const imgFrame = styled.img`
    background-repeat: no-repeat;
    background-size: cover;
`

const BottomSmallLetter = styled.div`
  font-size: 20px;
  fontwight: 200;
  text-align: center;
`

const infoComponent = styled.div`
  
`
const InfoComponent = ({image, bottomWord})=>{
    return (
    <ImageBox><imgFrame src={JxAsia2020Img}/></ImageBox>
    )
}



class UpcomingEventsComponent extends Component {
  render() {
    return (
        <div class = "containerHiring"> 

            <div className="flexbox-upcomingevents">
            <div className="flexbox-left-upcomingevents">JuntionX Singapore 2019 Stats</div>
              {/* <div style={{width:20+'px'}}></div> */}
              <div className="flexbox-right-upcomingevents">
                <InfoComponent/>
              </div>
            </div>


            <div style={{'padding': 50+'px'}}></div>


            

        </div>
    );
  }
}

export default UpcomingEventsComponent;
